-- ----------------------------
-- Table structure for bk_admin
-- ----------------------------
DROP TABLE IF EXISTS `bk_admin`;
CREATE TABLE `bk_admin` (
  `admin_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `admin_name` varchar(100) NOT NULL DEFAULT '' COMMENT '管理员名称',
  `admin_tel` varchar(100) NOT NULL DEFAULT '' COMMENT '管理员电话',
  `admin_pwd` char(32) NOT NULL DEFAULT '' COMMENT '管理员密码',
  `last_ip` int(10) NOT NULL DEFAULT '0' COMMENT '最后登录ip',
  `last_time` int(10) NOT NULL DEFAULT '0' COMMENT '最后登录时间',
  `create_time` int(10) NOT NULL DEFAULT '0' COMMENT '创建时间',
  PRIMARY KEY (`admin_id`),
  KEY `admin_name` (`admin_name`),
  KEY `admin_tel` (`admin_tel`)
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8 COMMENT='管理员表';

