-- ----------------------------
-- Table structure for bk_article
-- ----------------------------
DROP TABLE IF EXISTS `bk_article`;
CREATE TABLE `bk_article` (
  `article_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `category_id` tinyint(1) NOT NULL DEFAULT '0' COMMENT '文章分类ID',
  `article_desc` varchar(300) NOT NULL DEFAULT '' COMMENT '文章介绍 用于列表页',
  `article_title` varchar(500) NOT NULL DEFAULT '' COMMENT '文章标题',
  `article_img` varchar(255) NOT NULL DEFAULT '' COMMENT '封面图片',
  `article_content` text COMMENT '文章内容',
  `article_recommend` tinyint(1) NOT NULL DEFAULT '0' COMMENT '站长推荐 0-否 1-是',
  `article_isindex` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否首页 0-否 1-是',
  `article_pv` int(11) NOT NULL DEFAULT '0' COMMENT '访问量',
  `article_updatetime` int(10) NOT NULL DEFAULT '0' COMMENT '修改时间',
  `article_addtime` int(10) NOT NULL DEFAULT '0' COMMENT '添加时间',
  PRIMARY KEY (`article_id`),
  KEY `article_title` (`article_title`)
) ENGINE=InnoDB AUTO_INCREMENT=23 DEFAULT CHARSET=utf8;

