DROP TABLE IF EXISTS `bk_article_tag`;
CREATE TABLE `bk_article_tag` (
  `aid` int(10) NOT NULL DEFAULT '0' COMMENT '文章ID',
  `tid` int(10) NOT NULL DEFAULT '0' COMMENT '标签ID',
